import React, { useState, useEffect } from 'react';
import clsx from 'clsx';
import PropTypes from 'prop-types';
import moment from 'moment';
import PerfectScrollbar from 'react-perfect-scrollbar';
import { makeStyles } from '@material-ui/styles';
import {
    Button,
    Card,
    CardActions,
    CardContent,
    Table,
    TableBody,
    TableCell,
    TableHead,
    TablePagination,
    TableRow,
    Typography,
    Avatar
} from '@material-ui/core';
import PersonIcon from '@material-ui/icons/Person';

import Dialog from '@material-ui/core/Dialog';
import DialogActions from '@material-ui/core/DialogActions';
import DialogContent from '@material-ui/core/DialogContent';
import DialogContentText from '@material-ui/core/DialogContentText';
import DialogTitle from '@material-ui/core/DialogTitle';

import { DocumentService } from 'service';
import { saveAs } from 'file-saver';

const useStyles = makeStyles(theme => ({
    root: {},
    content: {
        padding: 0
    },
    inner: {
        minWidth: 1050
    },
    nameContainer: {
        display: 'flex',
        alignItems: 'center'
    },
    avatar: {
        marginRight: theme.spacing(1)
    },
    actions: {
        justifyContent: 'flex-end'
    },
    tableCell: {
        width: '70%'
    }
}));

const DocumentListTable = props => {
    const { className, ...rest } = props;

    const classes = useStyles();

    const [rowsPerPage, setRowsPerPage] = useState(10);
    const [page, setPage] = useState(0);
    const [publicDocumentList, setPublicDocumentList] = React.useState([]);

    const handlePageChange = (event, page) => {
        setPage(page);
    };

    const handleRowsPerPageChange = event => {
        setPage(0);
        setRowsPerPage(event.target.value);
    };

    useEffect(() => {
        DocumentService.getPublicDocuments().then(data => {
            setPublicDocumentList(data.docs);
        }).catch((e) => {
            console.log(e);
        });
    }, []);

    const startDownload = (documentId) => {
        DocumentService.downloadDocument(documentId).then(data => {
            var blob = new Blob([data.csvData], {type: 'text/csv'});
            saveAs(blob, data.fileName);
        }).catch((e) => {
            console.log(e);
        });
    }

    return (
        <Card
            {...rest}
            className={clsx(classes.root, className)}
        >
            <CardContent className={classes.content}>
                <PerfectScrollbar>
                    <div className={classes.inner}>
                        <Table>
                            <TableHead>
                                <TableRow>
                                    <TableCell className={classes.tableCell}>Name</TableCell>
                                    <TableCell className={classes.tableCell}>Owner</TableCell>
                                    <TableCell align="center">Upload Date</TableCell>
                                    <TableCell align="right"></TableCell>
                                    <TableCell align="right"></TableCell>
                                </TableRow>
                            </TableHead>
                            <TableBody>
                                {publicDocumentList.slice((page * rowsPerPage), ((page + 1) * rowsPerPage)).map(document => (
                                    <TableRow
                                        className={classes.tableRow}
                                        hover
                                        key={document.id}
                                    >
                                        <TableCell className={classes.tableCell}>
                                            <div className={classes.nameContainer}>
                                                <Typography variant="body1">{document.title}</Typography>
                                            </div>
                                        </TableCell>
                                        <TableCell className={classes.tableCell}>
                                            <div className={classes.nameContainer}>
                                                <Avatar className={classes.avatar}><PersonIcon /></Avatar>
                                                <Typography variant="body1">{document.owner}</Typography>
                                            </div>
                                        </TableCell>
                                        <TableCell align="center">
                                            {moment(document.createdAt).format('DD/MM/YYYY')}
                                        </TableCell>
                                        <TableCell align="right">
                                            <Button
                                                onClick={() => alert('it will redirect to the editor :)')}
                                                color="primary"
                                                variant="outlined"
                                                className={classes.importButton}>
                                                View
                                            </Button>
                                        </TableCell>
                                        <TableCell align="right">
                                            <Button
                                                onClick={() => startDownload(document.id)}
                                                color="primary"
                                                variant="outlined"
                                                className={classes.importButton}>
                                                Download
                                            </Button>
                                        </TableCell>
                                    </TableRow>
                                ))}
                            </TableBody>
                        </Table>
                    </div>
                </PerfectScrollbar>
            </CardContent>
            <CardActions className={classes.actions}>
                <TablePagination
                    component="div"
                    count={publicDocumentList.length}
                    onChangePage={handlePageChange}
                    onChangeRowsPerPage={handleRowsPerPageChange}
                    page={page}
                    rowsPerPage={rowsPerPage}
                    rowsPerPageOptions={[5, 10, 25]}
                />
            </CardActions>
        </Card>
    );
};

DocumentListTable.propTypes = {
    className: PropTypes.string,
    users: PropTypes.array.isRequired
};

export default DocumentListTable;
